mapper.select:
SELECT SUM(sum) AS power FROM
(
    (
        SELECT MAX(cumulative_power) AS sum
        FROM ${tableName}
        WHERE index_num = ${index}
        AND date BETWEEN #{startTime} AND #{endTime}
    )
    UNION ALL
    (
        SELECT -MIN(cumulative_power) AS sum
        FROM ${tableName}
        WHERE index_num = ${index}
        AND date BETWEEN #{startTime} AND #{endTime}
    )
)AS total;

SELECT *
FROM device_power dp
WHERE dp.index  <>  0;

SELECT power
From ${table}
WHERE name = #{deviceName}
AND power  <>  0
LIMIT 10000;

SELECT *
FROM device_power dp
WHERE dp.index = ${index}
OR dp.name = #{name};
